#!/usr/bin/env bash
# daveisdigital@gmail.com
# This code should pass the 'shellcheck' bash linter

commands=(base64 bunzip2 stty)

for c in "${commands[@]}"
do
	command -v "$c" > /dev/null|| 
		{
			echo "ERROR: I require '$c' to run. Try again after you install it." ; exit
		}
done

cols=$(stty size | awk '{print $2}')
halfwidth=$(($cols / 2))

echo "Your terminal column is set to $cols | halfwidth=$halfwidth"

DF=""

function contact () {
	printf "%-${halfwidth} " "[ Dave Ingram | 217-693-8814 | daveisdigital@gmail.com ]\n"

}

function smile () {
	echo
         echo "$1" | base64 --decode | bunzip2
}

contact
smile $DF

